import React, { Component } from 'react';

class Search extends Component {
	// whenever we have a form, we need to have some sort of state for any of the form fields that will be changed by the user
	state = {
		text: '',
	};

	// here we are creating a change event; every time the text input in the form changes (someone types in something) we can capture that event through the DOM (e.target.value); the e captures each individual event change that happens
	onChange = (e) => {
		this.setState({ text: e.target.value });
	};

	render() {
		return (
			<div>
				<form className='form'>
					<input
						type='text'
						name='text'
						placeholder='Search for a User...'
						value={this.state.text}
						onChange={this.onChange}
					/>

					<input
						type='submit'
						className='btn btn-dark btn-block'
						name='submit'
						value='Search'
					/>
				</form>
			</div>
		);
	}
}

export default Search;
