import React, { Component } from 'react';

class Search extends Component {
	state = {
		text: '',
	};
	render() {
		return (
			<div>
				<form className='form'>
					<input
						type='text'
						name='text'
						placeholder='Search for a User...'
						value={this.state.text}
					/>

					<input
						type='submit'
						className='btn btn-dark btn-block'
						name='submit'
						value='Search'
					/>
				</form>
			</div>
		);
	}
}

export default Search;
