// react elments
import React, { useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
// styles
import './App.css';
// pages
import About from './components/pages/About';
// layout components
import NavBar from './components/layout/NavBar';
import Alert from './components/layout/Alert';
// user components
import Users from './components/users/Users';
import User from './components/users/User';
import Search from './components/users/Search';
// state components
import GithubState from './context/github/GithubState';
import AlertState from './context/alert/AlertState';

const App = () => {
	// all this is moved to githubState
	const [alert, setAlert] = useState(null);

	// search github user --  moved to GithubState.js

	//get single github users -- moved to githubState.js

	// get user repos -- moved to githubState.js

	// clear users -- moved to githubState.js

	// show Alert -- moved to githubState.js
	const showAlert = (msg, type) => {
		setAlert({ msg, type });
		setTimeout(() => setAlert(null), 5000);
	};

	return (
		<GithubState>
			<AlertState>
				<Router>
					<div className='App'>
						<nav className='navbar bg-primary'>
							<NavBar />
						</nav>
						<div className='container'>
							<Alert alert={alert} />
							<Switch>
								<Route
									exact
									path='/'
									render={(props) => (
										<>
											<Search setAlert={showAlert} />
											<Users />
										</>
									)}
								/>
								<Route exact path='/about' component={About} />
								<Route exact path='/user/:login' component={User} />
							</Switch>
						</div>
					</div>
				</Router>
			</AlertState>
		</GithubState>
	);
};

export default App;
