import React, { Component } from 'react';
import axios from 'axios';
import NavBar from './components/layout/NavBar';
import Users from './components/users/Users';
import Search from './components/users/Search';
import './App.css';

class App extends Component {
	state = {
		users: [],
		loading: false,
	};

	// using this.state because this is a class component
	async componentDidMount() {
		this.setState({ loading: true });
		const res = await axios.get(
			`https://api.github.com/users?client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`
		);
		this.setState({ users: res.data, loading: false });
	}
	// here we have drilled UP this.state.text (which is a component-level state) from the Search.js page and so we refer to this "prop" as 'text' so we can complete the code needed to change the text state
	searchUsers = (text) => {
		console.log(text);
	};

	render() {
		return (
			<div className='App'>
				<nav className='navbar bg-primary'>
					<NavBar />
				</nav>
				<div className='container'>
					<Search searchUsers={this.searchUsers} />
					<Users loading={this.state.loading} users={this.state.users} />
				</div>
			</div>
		);
	}
}
export default App;
